@use '../abstracts';
@use '../core';

body {
    background: abstracts.$primary-color;
    color: abstracts.set-text-color(abstracts.$primary-color);
    height: 100vh;
    font-family: core.$font-family;
    line-height: 1;
}

h1, h2, h3 {
    font-weight: 400;
}

a {
    color: abstracts.set-text-color(abstracts.$primary-color);
    text-decoration: none;
}

.text-secondary {
    color: abstracts.$secondary-color;
}

header {
    position: fixed;
    z-index: 2;
    width: 100%;
    padding: 1rem;
}

main {
    height: 100%;
    width: 100%;

    .social-icons {
        position: fixed;
        bottom: 1rem;
        left: 1rem;

        a {
            padding: 0.4rem;
            @include abstracts.transition-ease;

            &:hover {
                color: abstracts.$secondary-color;
            }
        }
    }
}

footer {
    font-size: 1rem;
    position: fixed;
    bottom: 0.4rem;
    right: 1rem;
    text-align: right;
    padding: 1rem;
    color: abstracts.set-text-color(abstracts.$primary-color);
}

