@use '../abstracts';

@include abstracts.media-md {
    .menu-button {
        visibility: hidden;
    }

    .nav {
        visibility: visible;

        .menu-nav {
            display: block;
            transform: translateY(0);
            height: 100%;
            background: transparent;
            text-align: right;

            &__item {
                display: inline;
                padding-right: 1.5rem;
            }

            &__link {
                font-size: 1.5rem;
            }
        }
    }

    .about__bio {
        font-size: 1.5rem;
    }

    .projects {
        &__bio-image {
            height: 40vh;
        }

        &__items {
            grid-template-columns: repeat(2, 1fr) !important;
        }

        .text-secondary {
            font-size: 3rem;
        }
    }

    .contact__list {
        grid-template-columns: repeat(2, 1fr) !important;
    }
}

@include abstracts.media-lg {
    .projects {
        &__items {
            grid-template-columns: repeat(3, 1fr) !important;
        }
    }

    .contact__list {
        grid-template-columns: repeat(3, 1fr) !important;
    }
}

@include abstracts.media-xl {
    .projects__bio-image {
        height: 50vh;
    }
}

