@use '../abstracts';

.about {
    padding-bottom: 2rem;

    &__bio-image {
        height: 50vh;
        width: 100%;
        background: linear-gradient(
                to right,
                rgba(abstracts.$primary-color, 0.9),
                rgba(abstracts.$primary-color, 0.3)
            ),
            url('../../src/images/model-1.jpg') center top;
        background-size: cover;
        display: flex;
        flex-direction: column;
        justify-content: flex-end;
        align-items: center;

        p {
            margin-bottom: 2rem;
        }
    }

    &__bio {
        width: 80%;
        text-align: center;

        .text-secondary {
            padding-bottom: 1rem;
        }
    }

    .jobs {
        width: 60vw;
        margin: 2rem auto 0 auto;
        display: grid;
        grid-template-columns: 1fr;
        grid-gap: 2rem;

        &__job {
            background: lighten(abstracts.$primary-color, 10%);
            padding: 0.5rem;
            border-bottom: 5px solid abstracts.$secondary-color;

            h2, h3 {
                margin-block: 0.5rem;
            }

            h6 {
                margin-block: 0.3rem;
            }
        }
    }

    .social-icons {
        display: flex;
        flex-direction: column;
    }

}

