@use '../abstracts';

.projects {
    padding-bottom: 2rem;

    &__bio-image {
        height: 50vh;
        width: 100%;
        background: linear-gradient(to right,
                rgba(abstracts.$primary-color, 0.9),
                rgba(abstracts.$primary-color, 0.3)),
            url('../../src/images/model-1.jpg') center top;
        background-size: cover;
        display: flex;
        flex-direction: column;
        justify-content: flex-end;
        align-items: center;

        .text-secondary {
            font-size: 2rem;
            margin-bottom: 2rem;
        }
    }

    &__items {
        width: 60vw;
        margin: 2rem auto 0 auto;
        display: grid;
        grid-template-columns: 1fr;
        grid-gap: 2rem;
    }

    &__item {
        position: relative;
        border-bottom: 5px solid abstracts.$secondary-color;
        overflow: hidden;
        cursor: pointer;

        img {
            width: 100%;
        }

        &::after {
            content: "";
            position: absolute;
            top: 100%;
            left: 0;
            height: 100%;
            width: 100%;
            background: abstracts.$secondary-color;
            opacity: 0;
            @include abstracts.transition-ease;
        }

        &:hover {
            &::after {
                top: 0;
                opacity: 0.9;
            }
            .projects__button {
                opacity: 1;
            }
        }
    }

    &__buttons {
        position: absolute;
        top: 0;
        height: 100%;
        width: 100%;
        z-index: 1;
        display: grid;
        grid-template-columns: repeat(2, 1fr);
        align-items: center;
        text-align: center;
    }

    &__button {
        opacity: 0;
        color: abstracts.set-text-color(abstracts.$secondary-color);
        @include abstracts.transition-ease;

        &:hover {
            color: abstracts.set-text-color(abstracts.set-text-color(abstracts.$secondary-color));
        }

    }

    .social-icons {
        display: flex;
        flex-direction: column;
    }

}

